uint8_t glcd_read_byte(uint16_t col, uint8_t pag)
{
	DDRD=0x00; //Input to read sram data
	//PORTD=0xff; //pullups
	
	uint8_t tempdata=0;
	
	glcd_set_page(pag);
	glcd_set_column(col);
	
	DISPLAY_DATA;	//set dc pin according date to be sent
	CS_HIGH;		//enable lcd
	RD_LOW;			//read data
	WR_HIGH; 		//latch data into controller	
	WR_LOW;
	tempdata=PIND;
	CS_LOW;			//disable lcd
	
	DDRD=0xff; //output
	PORTD=0x00; //low
	
	return tempdata;
}


